import {connect} from 'react-redux'
import React, {Component} from 'react';
import {loadInitialDataSocket} from '../actions/ChatTEST.js';
import {addNewItemSocket} from '../actions/ChatTEST.js';

class ChatTEST extends Component{
  constructor(props) {
    super(props);
    let socket = io.connect('http://localhost:3000')
    dispatch(loadInitialDataSocket(socket))

    socket.on('itemAdded', (res) =>{
      dispatch(AddItem(res))
    })
    io.socket.on('itemMarked', (res) =>{
      dispatch(completeItem(res))
    })
  }
  componentWillUnmount(){
    socket.disconnect();
