import React from 'react';
import {Component} from 'react';
import {connect} from 'react-redux';
import logo from '../pics/foxnetWhite.png';
import message from '../pics/sendMessage.png';
import MessagePopup from './MessagePopup.js';
import {getFriends} from '../actions/friends.js';

class FriendEl extends Component{
  componentDidMount(){
    this.props.onGetFriends('/friend')
  }
  render(){
    return(
      <React.Fragment>
      <div className='friendWrap'>
        <div className='friendInfo'>
          <img src={logo} alt='FriendPhoto'/>
          <div>
            <h3><a href="#">Тест Игорь</a></h3>
            <span>online</span>
          </div>
        </div>
        <div className='friendButtons'>
          <button  onClick={this.props.onTogglePopup}><img src={message}/></button>
          <button>X</button>
        </div>
      </div>
      </React.Fragment>
    )
  }
}
export default connect(
  state =>({

  }),
  dispatch =>({
    onGetFriends: (url) =>{
      dispatch(getFriends(url))
    },
    onTogglePopup: () =>{
      dispatch({type: 'TOGGLE_MESSAGE_POPUP', status: 'flex'})
    }
  })
)(FriendEl)
