import React from 'react';
import {Component} from 'react';

class Input extends Component{
  constructor(props){
    super(props);
    this.state = {ref: this.props.ref}
  }
  render(){
    return <input type={this.props.type} className={this.props.className} ref={(input) => {this.state.ref= input}} placeholder={this.props.placeholder}/>
  }
}

export default Input
