import React from 'react';
import {Component} from 'react';
import {connect} from 'react-redux';
import userPhoto from '../pics/profilePhoto.jpg'


class Chat extends Component{
  render(){
    let messages = this.props.messages.map((item,key) =>{
      return <div className={this.props.sender[key]} key={key}><p>{item}</p> <span className='messageTime'>{this.props.time[key]}</span></div>
    });
    console.log(messages);
    return(
      <div className="dialogMessages">
        <div className="messageProfileInfo">
          <img src={userPhoto} alt="PHOTO"/>
          <h4>{this.props.interlocutor}</h4>
        </div>
        <p className='isOnline'>online</p>
        <div className="dialogBlock">
          <div className='yourMessage'><p></p> <span className='messageTime'>13:01</span></div>
          <div className='interlocutorMessage'><p></p><span className='messageTime'>13:09</span></div>
          <div className='yourMessage'><p></p><span className='messageTime'>13:10</span></div>
          <div className='yourMessage'><p></p><span className='messageTime'>13:10</span></div>
          <div className='interlocutorMessage'><p></p><span className='messageTime'>13:11</span></div>
          <div className='interlocutorMessage'><p></p><span className='messageTime'>13:32</span></div>
          <div className='yourMessage'><p></p><span className='messageTime'>13:33</span></div>
          <div className='interlocutorMessage'><p></p><span className='messageTime'>13:33</span></div>
          <div className='yourMessage'><p></p><span className='messageTime'>13:34</span></div>
          <div className='interlocutorMessage'><p></p><span className='messageTime'>13:35</span></div>
          <div className='interlocutorMessage'><p></p><span className='messageTime'>13:35</span></div>
          <div className='interlocutorMessage'><p></p><span className='messageTime'>13:35</span></div>
          <div className='interlocutorMessage'><p></p><span className='messageTime'>13:35</span></div>
          <div className='yourMessage'><p></p><span class='messageTime'>13:35</span></div>
          <div className='interlocutorMessage'><p></p><span className='messageTime'>13:35</span></div>
        </div>
        <input type="text" placeholder="Enter your message" className='messageInput'/>
      </div>
    )
  }
}

export default connect(
  state =>({
    interlocutor: state.dialog.interlocutor,
    messages: state.messagesList.messages,
    time: state.messagesList.time,
    sender: state.messageList.sender
  }),
  dispatch =>({})
)(Chat)
