import {createStore} from 'redux';

function playList(state = [], action) {
  if (action.type === 'ADD_TRACK'){
    return[
      ...state,
      action.playload
    ]
  }
}

const store = createStore(playList);

store.subscribe(() =>{
  console.log('subscribe', store.getState());
})

store.dispatch({'type': 'ADD_TRACK', 'playload': 'SLTS'})
