import React from 'react';
import {Component} from 'react';

class Input extends Component{
  render(){
    const ref = this.props.refs;
    console.log(ref)
    console.log(this.ref)
    return <input type={this.props.type} className={this.props.className} ref={(input) => {this.ref= input}} placeholder={this.props.placeholder}/>
  }
}

export default Input
