export function fetchSuccess(success){
  localStorage.setItem('email',JSON.stringify(success));
}

export function authorisation(url, data){
  return (dispatch) => {
    return fetch(url, {
      method: 'POST',
      body: JSON.stringify(data),
      headers: new Headers({
        'Content-Type': 'application/json'
      }),
    })
    .then(response =>{
      if(!response.ok){
        throw new Error(response.statusText)
      }
      return response;
    })
    .then(response => response.json())
    .then(success => fetchSuccess(success))
  }
}
