import React from 'react';
import {Component} from 'react';
import {connect} from 'react-redux';

class Registration extends Component{
  constructor(props){
    super(props);
    this.state = {
      days:[],
      months:['January','February','March','April','May','June','July','August','September','October','November','December'],
      years:[]
    };
  }
  componentDidMount(){
    for(let i=1; i<=31; i++){
      this.state.days.push(i);
    }
    this.setState({days: this.state.days});
    for(let i=2019; i>=1900; i--){
      this.state.years.push(i);
    }
    this.setState({years: this.state.years})
  }
  render(){
    let listDays = this.state.days.map((item, key) =>{
      return <option value={item} key={key}>{item}</option>
    })
    let listMonth = this.state.months.map((item,key) =>{
      return <option value={item} key={key}>{item}</option>
    })
    let listYears = this.state.years.map((item,key) =>{
      return <option value={item} key={key}>{item}</option>
    })
    return (
      <div className="singWrap">
        <form className='autorisationWrap registr' action="">
            <h1>Registration</h1>
            <input type="text" class="loginData" placeholder="Firstname"/>
            <input type="text" class="loginData" placeholder="Email"/>
            <div class="loginData selectDateWrap">
              <select class="selectDate" name="">
                {listDays}
              </select>
              <select class="selectDate" name="">
                {listMonth}
              </select>
              <select class="selectDate" name="">
                {listYears}
              </select>
            </div>
            <input type="text" class="loginData" placeholder="Password"/>
            <input type="text" class="loginData" placeholder="Password again"/>
            <input type="submit" class='logInput regInput' value='REGISTRATE'/>
        </form>
      </div>
    )
  }
}

export default Registration;
