export const initialItems = (res) => ({
  type: "INITIAL_ITEMS",
  items: res
})


export const loadInitialDataSocket = (socket) => {
  return (dispatch) => {
    socet.on('initialList', (res) =>{
      console.dir(res)
      dispatch(initialItems(res))
    })
  }
}

export const addNewItemSocket = (socket, id, item) =>{
  return (dispatch) => {
    let postData ={
      id: id+1,
      item: item,
      completed: false
    }
    socket.emit('addItem', postData)
  }
}
