export function authorisation(url){
  return (dispatch) => {
    fetch(url, {
      method:'post',
      body: JSON.stringify({login: 'login', password: '4'}),
      headers: {
        'content-type':'aplication/json'
      }
    })
    .then(response =>{
      if (response.status !== 200){
        return Promise.reject();
      }
      return response.json();
    })
    .then(function (data){
      console.log(JSON.stringify(data))
    })
    .catch(() => console.log('ошибка'))
  }
}
